    private void btnordenarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnordenarActionPerformed
     
        try{
        int numeros[] = new int[9];

        int numero1 = Integer.parseInt(this.txtnumero1.getText());
        int numero2 = Integer.parseInt(this.txtnumero2.getText());
        int numero3 = Integer.parseInt(this.txtnumero3.getText());
        int numero4 = Integer.parseInt(this.txtnumero4.getText());
        int numero5 = Integer.parseInt(this.txtnumero5.getText());
        int numero6 = Integer.parseInt(this.txtnumero6.getText());
        int numero7 = Integer.parseInt(this.txtnumero7.getText());
        int numero8 = Integer.parseInt(this.txtnumero8.getText());
        int numero9 = Integer.parseInt(this.txtnumero9.getText());

        numeros[0] = numero1;
        numeros[1] = numero2;
        numeros[2] = numero3;
        numeros[3] = numero4;
        numeros[4] = numero5;
        numeros[5] = numero6;
        numeros[6] = numero7;
        numeros[7] = numero8;
        numeros[8] = numero9;


        boolean flag = true;
        int temp;

        while (flag) {
            flag = false;
            for (int i = 0; i < numeros.length - 1; i++) {

                if (numeros[i] > numeros[i + 1]) {
                    temp = numeros[i];
                    numeros[i] = numeros[i + 1];
                    numeros[i + 1] = temp;
                    flag = true;

                }
            }
        }
             int numbers[][] = new int[3][3];
           
            numbers[0][0] = numeros[0];
            numbers[0][1] = numeros[1];
            numbers[0][2] = numeros[2];
            numbers[1][0] = numeros[3];
            numbers[1][1] = numeros[4];
            numbers[1][2] = numeros[5];
            numbers[2][0] = numeros[6];
            numbers[2][1] = numeros[7];
            numbers[2][2] = numeros[8];

            
            txtnumber1.setText(String.valueOf(numbers[0][0]));
            txtnumber2.setText(String.valueOf(numbers[0][1]));
            txtnumber3.setText(String.valueOf(numbers[0][2]));
            txtnumber4.setText(String.valueOf(numbers[1][0]));
            txtnumber5.setText(String.valueOf(numbers[1][1]));
            txtnumber6.setText(String.valueOf(numbers[1][2]));
            txtnumber7.setText(String.valueOf(numbers[2][0]));
            txtnumber8.setText(String.valueOf(numbers[2][1]));
            txtnumber9.setText(String.valueOf(numbers[2][2]));
            } catch (NumberFormatException ex) {

           JOptionPane.showMessageDialog(this, " no se puede realizar la operaci√≥n, "
                    + " puede que no halla ingresado valores "
                    + "\n o los valores ingresados no son validos", "ERROR", JOptionPane.ERROR_MESSAGE);
        }

        // TODO add your handling code here:
    }//GEN-LAST:event_btnordenarActionPerformed

